import React, {Component} from 'react';

class AddPlayerForm extends Component {
    state = {
        value : ''
    };
    
    render(){
        return(
            <form>
                <input 
                    type= "text"
                    placeholder= "Enter a player's name"
                    value= {this.state.value}
                />
                <input 
                    type= "submit"
                    value= "Add player"
                />
            </form>
        );
    }

}

export default AddPlayerForm;
